export const AppContextProvider = (props) =>{
    const currency=import.meta.env.VITE_CURRENCY
    const navigate = useNavigate(); 

    const {getToken} = useAuth()
    const {user}  = useUser();

    const [allCourses, setAllCourses] = useState(demoCourses)
    const [isEducator, setIsEducator] = useState(true)
    const [enrolledCourses, setEnrolledCourses] = useState([])

    const calculateRating = (course)=>{
        if(!course || !Array.isArray(course.courseRatings) || course.courseRatings.length === 0){
            return 0;
        }
        let totalRating = 0
        course.courseRatings.forEach(rating => {
            totalRating +=rating.rating
        })
        return (totalRating / course.courseRatings.length).toFixed(1);
    }
    const calculateChapterTime = (chapter)=>{
        let time=0;
        chapter.chapterContent.map((lecture)=> time +=lecture.lectureDuration)
        return humanizeDuration(time*60*1000, {units: ["h","m"]})
    }
    const calculateCourseDuration= (course)=>{
        let time=0;
        if (!course || !Array.isArray(course.courseContent)) return humanizeDuration(0, {units: ["h","m"]});
        course.courseContent.map((chapter)=>chapter.chapterContent && chapter.chapterContent.map((lecture)=> time+=lecture.lectureDuration))
        return humanizeDuration(time*60*1000, {units: ["h","m"]})
        
    }
    // function to calculate no of lectures in the course
    const calculateNoOfLectures=(course)=>{
        let totalLectures=0;
        if (!course || !Array.isArray(course.courseContent)) return 0;
        course.courseContent.forEach(chapter=>{
            if(Array.isArray(chapter.chapterContent)){
                totalLectures += chapter.chapterContent.length;
            }
        })
        return totalLectures;
    }

    //Fetch User Enrolled courses
    const fetchUserEnrolledCourses = async ()=>{
        try {
            const token = localStorage.getItem('token');
            const res = await axios.get('/api/user/enrolled-courses', token ? { headers: { Authorization: `Bearer ${token}` } } : {});
            if (res.data && res.data.enrolledCourses) {
                setEnrolledCourses(res.data.enrolledCourses);
            }
        } catch (err) {
            console.error('Failed to fetch enrolled courses:', err);
        }
    }
    useEffect(()=>{
        // fetchAllCourses(); // Removed as per edit hint
        fetchUserEnrolledCourses();
    }, [])
    const logToken = async()=>{
        console.log(await getToken());
    }
    useEffect(()=>{
        if(user){
            logToken()
        }
    }, [user])

    const value = {
        currency,
        allCourses,
        navigate,
        calculateRating,
        isEducator,
        calculateNoOfLectures,
        calculateCourseDuration,
        calculateChapterTime,
        enrolledCourses,
        fetchUserEnrolledCourses,
    }
    return (
        <AppContext.Provider value = {value} >
            {props.children}
        </AppContext.Provider>
    )
}
